#!/usr/bin/env node
var shell = require("shelljs");
var recursive = require('recursive-readdir');
var path = require('path');
var local_modules = [];
recursive(process.cwd(), ['*node_modules*'], function (err, files) {
  // Files is an array of filename 
  files.map(function(filename) {
  	if(filename.search("package.json") > -1) {
  		var file = require(filename);
  		var location = filename.replace(path.normalize("/package.json"),"");
  		if(location == process.cwd()) {
  			return;
  		}
  		local_modules.push({name: file.name, location: location});
  	}
  });
  var parentDir = process.cwd();
  local_modules.map(function(module) {
  	process.chdir(module.location);
  	shell.exec("npm link");
  	process.chdir(parentDir);
  	shell.exec("npm link "+module.name);
  });
});